.macro	sound(%base_note, %note, %duration, %instrument, %volume)
	lw $a0, 0(%note)
	add $a0, $a0, %base_note
	lw $a1, 0(%duration)
	lw $a2, 0(%instrument)
	lw $a3, 0(%volume)
	jal SOUND
.end_macro
SOUND:
	li $v0, 31	#syscall for midi
	syscall
	jr $ra

.macro	sleep(%duration)
	lw $a0, 0(%duration)
	jal SLEEP
.end_macro
SLEEP:
	li $v0, 32	#syscall for sleep
	syscall
	jr $ra

MAIN:
	lw $s0, CONT		#quant de notas
	lw $s1, INIT		#nota inicial
	la $s2, NOTAS		#notas
	la $s3, DURACAO		#duracoes
	la $s4, DELAY		#delay
	la $s5, VOLUME		#volume
	la $s6, INSTRUMENTO	#instrumento
LOOP1:
	sound($s1,$s2,$s3,$s6,$s5)
	sleep($s4)

	beqz $s0, DEPOIS

	addiu $s0, $s0, -1
	addiu $s2, $s2, 4
	addiu $s3, $s3, 4
	addiu $s4, $s4, 4
